<template>
  <div class="individual">
    <div class="user-inofo">
      <div class="user-info-item">
        {{ username }}
      </div>
    </div>
    <grid :cols="3">
      <grid-item @click.native="goRemark">
        <img class="tab-icon" src="data:image/svg+xml;base64," alt="">
        <span class="grid-center">意见反馈</span>
      </grid-item>
      <grid-item @click.native="changeAccount">
        <img class="tab-icon" src="data:image/svg+xml;base64,">
        <span class="grid-center">切换账号</span>
      </grid-item>
      <grid-item v-if="!hasLogin" @click.native="goLogin">
        <img class="tab-icon" src="data:image/png;base64,">
        <span class="grid-center">登录</span>
      </grid-item>
      <grid-item v-if="hasLogin" @click.native="logout">
        <img class="tab-icon" src="data:image/svg+xml;base64," alt="">
        <span class="grid-center">登出</span>
      </grid-item>
    </grid>
  </div>
</template>

<script>
  import { Grid, GridItem, GroupTitle } from 'vux'

  export default {
    name: 'individual',
    components: {
      Grid,
      GridItem,
      GroupTitle
    },
    data () {
      return {
        hasLogin: false,
        username: '?'
      }
    },
    methods: {
      changeAccount () {
        localStorage.removeItem('username')
        localStorage.removeItem('token')
        this.$router.push('/login')
      },
      goLogin () {
        this.$router.push('/login')
      },
      logout () {
        localStorage.removeItem('username')
        localStorage.removeItem('token')
        this.$router.push('/')
      },
      goRemark () {
        if (this.hasLogin) {
          this.$router.push('/remark')
        } else {
          this.$router.push('/login')
        }
      }
    },
    created () {
      if (localStorage.getItem('token')) {
        this.hasLogin = true
        if (localStorage.getItem('username')) {
          this.username = localStorage.getItem('username')
        }
      }
    }
  }
</script>

